parameters.message:
=Greetings 👋,
Hope you're doing well! Here's your digest for this week's episode of Philoshopy This! 

<h2>🎙 Episode Summary</h2>
{{ $json.summary }}

<h2>💡 Topics Discussed</h2>
{{ $json.topics.join('\n') }}

<h2>❓ Questions to Ponder</h2>
{{ $json.questions.join('\n') }}

parameters.jsCode:
const inputItems = $input.all();
const topics = [];
const questions = [];
const summary = $('Summarize Transcript').item.json.response.output_text;
// Format Topics
for (const [index, topic] of inputItems.entries()) {
 const title = $('Topics').all()[index].json.topic

 topics.push(`
 <h3>${title}</h3>
 <p>${topic.json.output}</p>`.trim()
 )
}

// Format Questions
for (const question of $('Extract Topics & Questions').item.json.questions) {
 questions.push(`
 <h3>${question.question}</h3>
 <p>${question.why}</p>`.trim()
 )
}

return { topics, summary, questions }

