// Get the XML content from the input
const xmlContent = $input.first().json.content;

// Get the tag name from the Config node
const tagName = $node["Config"].json.prompt_type;

// Create regex patterns for both prompt and model within the main tag
const promptRegex = new RegExp(`<${tagName}>[\\s\\S]*?<prompt>([\\s\\S]*?)</prompt>[\\s\\S]*?</${tagName}>`, "i");
const modelRegex = new RegExp(`<${tagName}>[\\s\\S]*?<model>([\\s\\S]*?)</model>[\\s\\S]*?</${tagName}>`, "i");

// Use the match method to apply the regex patterns
const promptMatch = xmlContent.match(promptRegex);
const modelMatch = xmlContent.match(modelRegex);

// Create the output item with proper structure
let outputItem = {
  json: {
    prompt: null,
    model: null
  }
};

// Extract prompt content if found
if (promptMatch) {
  outputItem.json.prompt = promptMatch[1].trim();
}

// Extract model content if found
if (modelMatch) {
  outputItem.json.model = modelMatch[1].trim();
}

// Return the properly structured item
return [outputItem];

